	<h2>Jquery With Json </h2>
	<hr>
	<script>
		$(document).ready(function() {
			$(window).scroll(function() {
				if ($(this).scrollTop() > 60) {
					$("#top").fadeIn();
				} else {
					$("#top").fadeOut();
				}
			});
			$("#top").click(function() {
				$("html,body").animate({
					scrollTop: 0
				}, 800)
			});


		});

	</script>
